css:
th {
  text-align: center;
  vertical-align: middle;
}
.bold-text {
    font-weight: bold;
}
.text-weight{
    font-weight:600;
}
/* Backgroud color for hilighted cells */
.highlighted-column {
  background-color: #bfbfbf;
}
.hide-all-border {
    border-top:none;
    border-bottom:none;
    border-left:none;
    border-right:none;
}
.hide-side-top-border {
    border-top:none!important;
    border-left:none!important;
    border-right:none!important;
}
/* Emblem of Kuwait Logo */
.logo-right {
  width: 14%;
  height: 110px;
  background-size: cover;
  float: right;
}
