/* Styles which apply to the First table on the Top */
.header-table {
  width: 40%;
  float: left;

}
/* Styles which apply to the Main Table */
.main-table {
  width: 90%;
  margin-left: 20px;
  margin-top: 20px;
  
}
/* Kuwait Police Logo */
.logo {
  height: 110px;
}
/* Emblem of Kuwait Logo */
.logo-right {
  width: 14%;
  height: 110px;
  background-size: cover;
  float: right;
}
/* Styles for English Text */
.english-col {
  text-align: left;
}
/* Styles for Arabic Text */
.arabic-col {
  text-align: right;
  direction: rtl;
}
/* Backgroud color for hilighted cells */
.highlighted-column {
  background-color: #bfbfbf;
}
/* Declaration Area */
.declaration-container {
  position: relative;
}
.signature-spacing {
  height: 38px;
  width: 100%;
}
.signature-container {
  width: 100%;
  position: absolute;
  bottom: 0;
}
.left-signature {
  float: left;
  width: 50%;
  padding-left: 10px;
}
.right-signature {
  float: right;
  width: 50%;
}
.line {
  margin-top: 5px;
}
.bottom-header:before {
    content: "-";
    display: block;
    line-height: 1em;
    color: transparent;
}
.footer-description {
  height: 120px;
  text-align: right;
  direction: rtl;
}
.footer-description-left {
  float: left;
  width: 50%;
}
.footer-description-right {
  float: right;
  width: 50%;
}
