th {
  font-size: 18px;
  text-align: center;
}
/* Styles which apply to the First table on the Top */
.header-table {
  width: 55%;
  float: left;
}
/* Styles which apply to the Main Table */
.main-table {
  width: 90%;
  margin-left: 20px;
  margin-top: 20px;
}
/* Kuwait Police Logo */
.logo {
  width: 20%;
  height: 100px;
  background-image: url("/files/logo-moi.svg");
  float: left;
}
/* Emblem of Kuwait Logo */
.logo-right {
  width: 14%;
  height: 110px;
  background-image: url("/files/Emblem_of_Kuwait.svg");
  background-size: cover;
  float: right;
  margin-right: 30px;
}
/* Styles for English Text */
.english-col {
  text-align: left;
}
/* Styles for Arabic Text */
.arabic-col {
  text-align: right;
  direction: rtl;
}
/* Backgroud color for hilighted cells */
.highlighted-column {
  background-color: #bfbfbf;
}
/* Declaration Area */
.declaration-container {
  position: relative;
}
.signature-spacing {
  height: 38px;
  width: 100%;
}
.signature-container {
  width: 100%;
  position: absolute;
  bottom: 0;
}
.left-signature {
  float: left;
  width: 50%;
  padding-left: 10px;
}
.right-signature {
  float: right;
  width: 50%;
}
.line {
  margin-top: 5px;
}
.bottom-header:before {
    content: "-";
    display: block;
    line-height: 1em;
    color: transparent;
}
.footer-description {
  height: 200px;
  text-align: right;
  direction: rtl;
}
.footer-description-left {
  float: left;
  width: 50%;
}
.footer-description-right {
  float: right;
  width: 50%;
}

.details {
    font-size: 15px;
    font-family: Tahoma, sans-serif;;
    line-height: 150%;
}

.certificate-footer {
    font-size: 15px;
    font-family: Tahoma, sans-serif;
    line-height: 140%;
    margin-top: 120px;
}

.company-address {
    color: #666666;
    font-size: 15px;
    font-family: Tahoma, sans-serif;;
}

