css:
.purchase_request table{
	border: solid 1px;
	width: 100%;
}

.purchase_request td, .purchase_request tr{
	border: solid 1px;
}

.purchase_request .dark_bg{
	background-color: #B7B7B7; 
}

.purchase_request .gray_bg{
	background-color: #D9D9D9; 
}

.purchase_request .square{
	border: solid 1px;
    width: 10px;
    height: 10px;
    margin: auto;
}

.purchase_request .same_line{
	display: inline-block;
}

.purchase_request .gray_bg_and_fix_width{
	background-color: #D9D9D9;
}


.purchase_request .no_border{
	border:none;
}


.purchase_request .no_border td, .purchase_request .no_border tr{
	border:none;
}


.purchase_request .bottom_margin{
	margin-bottom: 5px;
}

