css:
.card-container-front{
  width:54mm;
  height: 86mm;
  background-size: contain;
  background-repeat: no-repeat;
}
.companyLogo{
    text-align: center;
    padding-bottom: 5px;
}
.companyLogo img{
    width: 48.5mm;
    height: auto;
}
.employeeAvatar{
	margin:auto;
	text-align:center;
}
.employeeAvatar img{
    width: 29mm;
    height: 38mm;
    border-radius: 8px;
    border: 1px solid black;
    position:relative;
}
.employeeName{
    width:100%;
	padding: 5px 0;
	font-size: 15px;
	text-align: center;
}
.name .designation{
	font-size: 12px;
	text-transform: uppercase;
}
.employeeInformation-front{
    width:55.2mm;
    background-color:#4c4c4c;
    color:white;
	text-align: center;

}
.card-container-back{
     width:54mm;
  height: 86mm;
  background-size: contain;
  background-repeat: no-repeat;
}
img{
    max-width: 100%;
    max-height: 100%;
}
.quote{
    border: 2px solid black; 
    margin: 4px;
    padding: 4px;
    font-size: 10px;
}
.moto{
    font-weight: bold;
}
.div2{
    display: flex;
    flex-direction: column; 
    width: 26.5mm;
}
.date{
    text-align: left;
    font-size: 6px;
    margin-left: 44mm;
    border:1px solid black;
    background-color:#979797;

}
.employeeInformation-back{
    background-color:#4c4c4c;
    color:white;
	margin: auto;
    padding: 0.25mm;
    width:55.2mm;
	text-align: center;
}
