frappe.web_form.on('job_title', (field, value) => {
    job_opening_id = frappe.web_form.get_value('job_title');
    frappe.web_form.set_df_property('job_title', 'hidden', 1);
    frappe.call({
      method: "one_fm.utils.get_job_opening",
      args:{
        'job_opening_id': job_opening_id
      },
      callback: function(r) {
        if(r.message){
					var job = r.message;
					frappe.web_form.set_df_property('one_fm_erf', 'hidden', 1);
					var fields = ['one_fm_erf', 'one_fm_source_of_hire', 'one_fm_sourcing_team']
					fields.forEach((field) => {
            frappe.web_form.set_value(field, job[field]?job[field]:'');
					});
				}
			}
    })
});

frappe.web_form.on('one_fm_first_name', (field, value) => {
    set_applicant_name();
});

frappe.web_form.on('one_fm_second_name', (field, value) => {
    set_applicant_name();
});

frappe.web_form.on('one_fm_third_name', (field, value) => {
    set_applicant_name();
});

frappe.web_form.on('one_fm_last_name', (field, value) => {
    set_applicant_name();
});

var set_applicant_name = function() {
  let name_fields = ['one_fm_second_name', 'one_fm_third_name', 'one_fm_last_name']
  let applicant_name = frappe.web_form.get_value('one_fm_first_name') ?frappe.web_form.get_value('one_fm_first_name'):'';
  name_fields.forEach((name_field) => {
    if(frappe.web_form.fields_dict[name_field].get_value()){
      applicant_name += ' '+frappe.web_form.fields_dict[name_field].get_value();
    }
  });
  frappe.web_form.set_value('applicant_name', applicant_name);
};

frappe.web_form.on('one_fm_erf', (field, value) => {
    erf = frappe.web_form.get_value('one_fm_erf');
    frappe.call({
      method: "one_fm.utils.get_erf",
      args:{
        'erf_id': erf
      },
      callback: function(r) {
        if(r.message){
          erf = r.message;
          set_uniform_fields(erf);
          set_erf_basic_details(erf);
          set_work_details_section(erf);
          validate_date_of_birth(erf.minimum_age_required);
        }
      }
    })
});

frappe.web_form.on('one_fm_source_of_hire', (field, value) => {
    source_of_hire = frappe.web_form.get_value('one_fm_source_of_hire');
    set_have_visa_field_properties();
    if(frappe.web_form.get_value('one_fm_source_of_hire') == 'One FM'){
			frappe.web_form.set_value('one_fm_agency', '');
			frappe.web_form.set_value('one_fm_is_agency_applying', false);
		}
    set_field_properties_on_agency_applying();
});

frappe.web_form.on('one_fm_gender', (field, value) => {
    set_height_field_property_from_gender();
});

frappe.web_form.on('one_fm_agency', (field, value) => {
  // if(frappe.web_form.get_value('one_fm_agency')){
