  // }
  // else{
  //   frappe.web_form.set_value('one_fm_is_agency_applying', false);
  // }
});

frappe.web_form.on('one_fm_is_agency_applying', (field, value) => {
    set_field_properties_on_agency_applying();
});

frappe.web_form.on('one_fm_cid_number', (field, value) => {
  validate_cid();
});

frappe.web_form.on('one_fm_date_of_birth', (field, value) => {
  validate_cid();
  validate_min_age();
});

frappe.web_form.on('one_fm_height', (field, value) => {
  validate_height_range();
});

frappe.web_form.on('one_fm_passport_issued', (field, value) => {
  validate_passport_date();
});

frappe.web_form.on('one_fm_passport_expire', (field, value) => {
  validate_passport_date();
});

frappe.web_form.on('one_fm_employment_start_date', (field, value) => {
  validate_employment_date();
});

frappe.web_form.on('one_fm_employment_end_date', (field, value) => {
  validate_employment_date();
});

frappe.web_form.on('one_fm_notice_period_in_days', (field, value) => {
  if(frappe.web_form.get_value('one_fm_i_am_currently_working') && (frappe.web_form.get_value('one_fm_notice_period_in_days') > 120)){
    frappe.web_form.set_value('one_fm_notice_period_in_days', '');
    frappe.throw(__("We are not looking for a Job Applicant having notice period greater than 120 Days"));
  }
});

var validate_employment_date = function() {
	if(frappe.web_form.get_value('one_fm_i_am_currently_working') && (frappe.web_form.get_value('one_fm_employment_end_date') < frappe.web_form.get_value('one_fm_employment_start_date'))){
		frappe.web_form.set_value('one_fm_employment_start_date', '');
		frappe.throw(__("Employment End Date cannot be before Employment Start Date"));
	}
};

var validate_passport_date = function() {
	if(frappe.web_form.get_value('one_fm_passport_expire') < frappe.web_form.get_value('one_fm_passport_issued')){
		frappe.web_form.set_value('one_fm_passport_expire', '');
		frappe.throw(__("Passport Expires on Date cannot be before Passport Issued on Date"));
	}
};

var validate_height_range = function() {
	if(frappe.web_form.get_value('one_fm_height') && frappe.web_form.get_value('one_fm_height') <= 10){
		frappe.web_form.set_value('one_fm_height', '');
		frappe.throw(__('Hieght Must be greter than 10cm.'));
	}
};

var validate_min_age = function() {
	var minimum_age_required = 18;
	if(frappe.web_form.get_value('one_fm_erf') && frappe.web_form.get_value('one_fm_gender')){
		frappe.call({
      method: "one_fm.utils.get_erf",
      args:{
        'erf_id': frappe.web_form.get_value('one_fm_erf')
      },
			callback: function(r) {
				if(r && r.message && r.message.gender_height_requirement){
					var items = r.message.gender_height_requirement;
					items.forEach((item, i) => {
						if(item.gender == frappe.web_form.get_value('one_fm_gender') && item.minimum_age > 0){
							minimum_age_required = minimum_age;
						}
					});
					validate_date_of_birth(minimum_age_required);
				}
			}
		})
	}
	else{
		validate_date_of_birth(minimum_age_required);
	}
};

var validate_cid = function() {
	if(frappe.web_form.get_value('one_fm_cid_number')){
		var valid_cid = true;
		if(frappe.web_form.get_value('one_fm_cid_number').length != 12 || !isNumeric(frappe.web_form.get_value('one_fm_cid_number'))){
			valid_cid = false;
		}
		else if (frappe.web_form.get_value('one_fm_date_of_birth')){
			if(!is_dob_include_in_cid(frappe.web_form.get_value('one_fm_cid_number'), frappe.web_form.get_value('one_fm_date_of_birth'))){
				valid_cid = false;
			}
		}
		if(!valid_cid){
			frappe.web_form.set_value('one_fm_cid_number', '');
			frappe.throw(__("Please Enter a Valid Civil ID."));
		}
	}
	function isNumeric(num){
	  return !isNaN(num)
	}
};

var is_dob_include_in_cid = function(cid, dob) {
	let	date_ms = Date.parse(dob);
	let	date_obj = new Date();
	date_obj.setTime(date_ms);
	let	year = date_obj.getFullYear();
	let month = date_obj.getMonth()+1;
	let day = date_obj.getDate();
	if(month < 10){
		month = '0'+month.toString();
	}
	else{
		month = month.toString();
	}
	if(day < 10){
		day = '0'+day.toString();
	}
	else{
		day = day.toString();
	}
	year = year.toString().slice(-2);
	let date_string = year+month+day;
	return cid.includes(date_string);
};

var set_height_field_property_from_gender = function() {
	if(frappe.web_form.get_value('one_fm_erf') && frappe.web_form.get_value('one_fm_gender')){
		frappe.call({
      method: "one_fm.utils.get_erf",
      args:{
        'erf_id': frappe.web_form.get_value('one_fm_erf')
      },
			callback: function(r){
				let show_height_field = false;
				if(r.message && r.message.gender_height_requirement){
					let height_details = r.message.gender_height_requirement;
					height_details.forEach((item) => {
						if(item.gender == frappe.web_form.get_value('one_fm_gender') && item.height > 0){
							show_height_field = true;
						}
					});
				}
				frappe.web_form.set_df_property('one_fm_height', 'hidden', show_height_field?false:true);
			}
		});
	}
	else{
		frappe.web_form.set_df_property('one_fm_height', 'hidden', true);
	}
};

var set_field_properties_on_agency_applying = function() {
	// On Contact Section
	let fields = ['one_fm_contact_number'];
	let hide_fields = ['one_fm_contact_number', 'one_fm_secondary_contact_number', 'one_fm_country_code', 'one_fm_country_code_second'];
	if(frappe.web_form.get_value('one_fm_agency') && frappe.web_form.get_value('one_fm_is_agency_applying')){
    set_hidden_fields(hide_fields, true);
	}
	else{
		set_hidden_fields(hide_fields, false);
	}

	// On Work Details Section
	if(frappe.web_form.get_value('one_fm_is_agency_applying')){
		frappe.web_form.set_df_property('one_fm_work_details_section', 'hidden', true);
	}
	else{
    erf = frappe.web_form.get_value('one_fm_erf');
    if(erf){
      frappe.call({
        method: "one_fm.utils.get_erf",
        args:{
          'erf_id': erf
        },
        callback: function(r) {
          if(r.message){
            erf = r.message;
            set_work_details_section(erf);
          }
        }
      })
    }
	}
};

var set_hidden_fields = function(fields, hidden) {
  fields.forEach((field) => {
  	frappe.web_form.set_df_property(field, 'hidden', hidden);
	});
};

var set_have_visa_field_properties = function() {
	if(frappe.web_form.get_value('one_fm_source_of_hire') == 'Local'){
		frappe.web_form.set_value('one_fm_have_a_valid_visa_in_kuwait', true);
	}
};

var set_uniform_fields = function(erf) {
  frappe.web_form.set_value('one_fm_is_uniform_needed_for_this_job', erf.is_uniform_needed_for_this_job);
};

var set_erf_basic_details = function(erf) {
	frappe.web_form.set_value('one_fm_hiring_method', erf.hiring_method);
};

var set_work_details_section = function(erf) {
	if(!frappe.web_form.get_value('one_fm_is_agency_applying')){
    if(erf.shift_working || erf.night_shift || erf.travel_required || erf.driving_license_required){
      frappe.web_form.set_df_property('one_fm_work_details_section', 'hidden', false);
  		frappe.web_form.set_df_property('one_fm_rotation_shift', 'hidden', !erf.shift_working);
  		frappe.web_form.set_df_property('one_fm_night_shift', 'hidden', !erf.night_shift);
  		frappe.web_form.set_df_property('one_fm_type_of_travel', 'hidden', !erf.travel_required);
  		if(erf.travel_required && erf.type_of_travel){
  			let options = ['I Will Travel '+erf.type_of_travel, 'I Cant Travel '+erf.type_of_travel];
  			frappe.web_form.set_df_property('one_fm_type_of_travel', 'options', options)
  		}
  		frappe.web_form.set_df_property('one_fm_type_of_driving_license', 'hidden', !erf.driving_license_required);
    }
	}
};

var validate_date_of_birth = function(minimum_age_required) {
	if(frappe.web_form.get_value('one_fm_date_of_birth')){
		let today = frappe.datetime.get_today()
		// Add minimum_age_required*12 Months to DOB to get date after 15 years
		let dob_present = frappe.datetime.add_months(frappe.web_form.get_value('one_fm_date_of_birth'), minimum_age_required*12)
		// If today >= dob_present, then age is more than minimum_age_required years
		if(today < dob_present){
			frappe.web_form.set_value('one_fm_date_of_birth', '');
			if(minimum_age_required == 18){
				frappe.throw(__("We Love to Work With Young Geniuses, Its Not Legal Though."));
			}
			else{
				frappe.throw(__("Minimum Age Should be {0} Years", [minimum_age_required]));
			}
		}
	}
};
